INSERT INTO `__locations` (`id`, `title`, `address`, `city`, `country`, `location`, `map_img`, `main_img`, `cover_img`, `description`, `public`, `created_at`, `updated_at`) VALUES
(2, 'Evropska kuća kulture i nacionalnih manjina', 'Patriotske Lige', 'Sarajevo', 21, 'https://maps.app.goo.gl/YNUN94YSgyqCG11C9', 'a8c1d638f59bbcda783cdd3f0abc1e83.png', '6d08ce9cd737349672325e680edb035a.jpg', '918130f0f6cbc865957319e143169508.jpg', '<p><font color=\"#f7f7f7\"><b style=\"\">Evropska kuća kulture i nacionalnih manjina </b>smještena u samom centru Sarajeva i predstavlja centralnu lokaciju održavanja <b style=\"\">Helem Nejse Talent Akademije</b>. </font></p><p><font color=\"#f7f7f7\">Prvi sprat je koncipiran kao višenamjenski prostor površine od skoro 250m2 unutar kojeg će se održavati program Critical Thinking i koji će predstavljati centralni hub za druženje polaznika, a gornji sprat nudi pet zasebnih \"kupea\", za rad u manjim, odvojenim grupama.</font></p>', 0, '2024-04-17 17:28:29', '2024-07-16 16:52:06'),
(7, 'Vila Braun', 'Alipašina 45', 'Sarajevo', 21, 'https://www.google.com/maps/place/Grafotisak+Sarajevo/@43.845233,18.2990679,15z/data=!4m6!3m5!1s0x4758ca4c3e7dc88d:0x8425e14c5d7b979f!8m2!3d43.8452335!4d18.3093678!16s%2Fg%2F11c4bdc41z?entry=ttu', '7aaa5bde1c06aca3b731e4651457a0de.png', '9c625cd7cd4545bed5316466474503ed.jpg', '6694ee365c44e5b721460f55056b6efd.jpg', '<div style=\"text-align: justify;\"><span style=\"text-align: var(--bs-body-text-align);\"><font color=\"#f7f7f7\">Vila Braun, u neposrednoj blizini <i>Evropske kuće kulture i nacionalnih manjina </i>predstavlja drugo primarno mjesto održavanja Helem Nejse Talent Akademije. Ova istaknuta lokacija, s velikom centralnom salom i nekoliko soba za sastanke predstavlja idealno mjesto za održavanje predavanja iz oblasti kreativnih industrija. Velika bašta i terasa ispred kuće će posebno obradovati polaznike tokom vrućih ljetnih dana.</font></span></div>', 1, '2024-04-18 16:37:03', '2024-05-15 20:23:59'),
(11, 'Univerzitet Burch (IBU)', 'Francuske revolucije bb, Ilidža', 'Sarajevo', 21, 'https://maps.app.goo.gl/Eh8ujC5GoXMKjaJL8', '0e5a82b44008a388629d07674cfd88be.png', 'adc652381b1ddb9e1cf7a1b0372c8069.jpg', 'd09ee801cbb8db1af5908763ec1d5562.jpg', '<div><span style=\"font-size: var(--bs-body-font-size); font-weight: var(--bs-body-font-weight); letter-spacing: 0px; text-align: var(--bs-body-text-align);\"><font color=\"#f7f7f7\">Međunarodni univerzitet Burch (IBU) je u vlasništvu Stirling Educationa, vodećeg pružatelja prvoklasnog obrazovanja širom svijeta, čiji je korporativni sjedište locirano u Londonu, Ujedinjeno Kraljevstvo.</font></span></div><div><span style=\"font-size: var(--bs-body-font-size); font-weight: var(--bs-body-font-weight); letter-spacing: 0px; text-align: var(--bs-body-text-align);\"><font color=\"#f7f7f7\"><br></font></span></div><div><font color=\"#f7f7f7\">Sa fakultetima inženjeringa, prirodnih i medicinskih nauka, ekonomije i društvenih nauka, te obrazovanja i humanističkih nauka, Međunarodni univerzitet Burch uživa visoki ugled i slijedi \'model preduzetničkog univerziteta\'; to jest, podstiče i podržava inovacije, prepoznaje i stvara prilike, te promoviše meke vještine i preduzetnički mindset.</font></div><div><font color=\"#f7f7f7\">Međunarodni univerzitet Burch naglašava akademski uspjeh te podstiče i inspiriše svoje studente da postanu najbolji što mogu biti. Istovremeno, Međunarodni univerzitet Burch gleda dalje od akademskog postignuća i prema razvoju karaktera, njegujući i razvijajući jake i otporne karaktere koji mogu misliti nezavisno, imati osjećaj za kreativnost i koji će prihvatiti i rješavati probleme.</font></div><div><font color=\"#f7f7f7\"><br></font></div><div><font color=\"#f7f7f7\">\"Kao organizacija, mi smo orijentisani ka budućnosti i prepoznajemo potrebu za prilagođavanjem u svijetu koji se neprestano mijenja tehnološki. Naš kurikulum je prilagođen tako da sadrži snažan digitalni element, podržan izvrsnim nastavnim planom i programom ICT-a i najnovijom tehnologijom u učionicama. Naša ponuda online i digitalnog nastavnog materijala bila je podvrgnuta testiranju pod najtežim globalnim pandemijskim uvjetima. <font style=\"font-size: var(--bs-body-font-size); font-weight: var(--bs-body-font-weight); letter-spacing: 0px; text-align: var(--bs-body-text-align);\">Izuzetno smo ponosni na ono što smo postigli i što možemo postići u narednim godinama.\" - <i>Ističu sa </i></font><span style=\"font-size: var(--bs-body-font-size); font-weight: var(--bs-body-font-weight); letter-spacing: 0px; text-align: var(--bs-body-text-align);\"><i style=\"\">Univerziteta Burch u Sarajevu. </i></span></font></div>', 1, '2024-05-10 09:30:28', '2024-05-16 10:21:53'),
(8, 'Helem Nejse Studio', 'Kobilja glava 35', 'Sarajevo', 21, 'https://maps.app.goo.gl/akaNw1Z6fZ12KHYm8', 'aff2a20676f14ec65975f4e178e8ae54.png', 'd86d426ab4ce6cc7f14448d9f34c18b1.jpg', 'afbf911f98f07d53bc3e2b6ecac49bdc.jpg', '<div style=\"text-align: justify;\"><font color=\"#f7f7f7\">Unutar novog Helem Nejse studija, polaznici odsjeka za muzičku produkciju bit će jedni od prvih koji će raditi na mjestu gdje bend snima muziku, ali i dizajnira zvuk i naraciju za regionalno popularni animirani serijal Bruca Braca Bruda Brada. Cilj je mladima prikazati multifunkcionalni studio namijenjen za produkciju više medija, što postaje standard rada u čitavom svijetu.</font></div>', 1, '2024-04-18 16:37:03', '2024-05-10 13:28:22'),
(12, 'Hotel Holiday', 'Zmaja od Bosne 4', 'Sarajevo', 21, 'https://maps.app.goo.gl/GyVHycDBCZLK763R8', 'd8215981f78dce79a600c5bedb5e2c76.png', '4179bf362e9fa43f99929a712e4b4cfb.jpg', 'c3ce3037df1f5176dbbf8d73224d09d9.jpg', '<div><font color=\"#f7f7f7\">Hotel Holiday je projektirao proslavljeni bosanski arhitekt Ivan Štraus u periodu 1982-1983 te je isti otvoren za XIV Zimske olimpijske igre Sarajevo 1984. godine i od tada je, kao svjedok novije historije Bosne i Hercegovine, postao nezaobilazno mjesto za poslovne putnike i turiste.</font></div><div><font color=\"#f7f7f7\"><br></font></div><div><font color=\"#f7f7f7\">Sljednik svjetskog hotelskog lanca i hotela Holiday Inn te ujedno i dio poznate grupacije Europe Group, na čelu sa Hotelom Europe, čija tradicija datira od davne 1882.godine, hotel Holiday će oduševiti goste svojom tradicionalnom elegancijom, vrhunskom uslugom i sadržajima.</font></div>', 1, '2024-05-10 13:39:00', '2024-05-10 13:41:36'),
(13, 'BIRN', 'Splitska 6', 'Sarajevo', 21, 'https://maps.app.goo.gl/R5k71GPCUKSxhhMe8', '50dac7b2859f2eccb6f762ec7e4c420e.png', '89d31dac283519f8c7d57acde401243b.jpg', '487faffec1a174bee6b2e6ef7fc9c7ea.jpg', '<p><font color=\"#f7f7f7\">U sklopu dana posvećenom posjetama redakcijama, odsjek za novinarstvo će imati priliku posjetiti i redakciju BIRNa, nevladine organizacije koja se specijalizirala za izvještavanje i monitoring vijesti o suđenjima za ratne zločine i korupciju u državi. Redakcija BIRNa predstavlja jedne od najvažnijih edukatora za odnose prema prošlosti, ali i edakvatnoj reportaži o aktuelnim društvenim pitanjima.</font></p>', 1, '2024-05-10 13:43:55', '2024-05-16 10:20:37'),
(14, 'CSEC Centar', 'Gradačačka 114', 'Sarajevo', 21, 'https://maps.app.goo.gl/wyH4egXuaFpTrNia7', '6357aed6d02c4d05069cd9964f4068f7.png', '245bac8258e94697501a40baa70dc4a0.jpg', '11aefda83c38d0f1228a334798c6a7b7.jpg', '<p><font color=\"#f7f7f7\">Misija CSEC-a je \"pozicionirati se kao neutralna, ključna tačka za sistematski odgovor na cyber incidente u Bosni i Hercegovini kako bi podržali razvoj i unapređenje cyber sigurnosti u Bosni i Hercegovini. CSEC ima misiju jačati komunikaciju između aktera u oblasti cyber sigurnosti te između CSIRT timova u regionu.</font></p><p><font color=\"#f7f7f7\">Centar za Izvrsnost u Cyber Sigurnosti (CSEC) je osnovan, kao dio Criminal Policy Research Centra, i uspostavljen je radi jačanja cyber sigurnosti u Bosni i Hercegovini. Cyber Security Excellence Centre funkcioniše uz podršku Vlade Ujedinjenog Kraljevstva.</font></p><p><font color=\"#f7f7f7\">Zajedno sa podrškom Vlade Ujedinjenog Kraljevstva, CSEC ima podršku DCAF-a i OEBS-a kako bi unaprijedio cyber sigurnost na Zapadnom Balkanu.</font></p><p><font color=\"#f7f7f7\">Trećeg dana naše akademije imat ćemo priliku posjetiti Centar za Izvrsnost u Cyber Sigurnosti (CSEC), gdje će cijeli dan biti posvećen temama cyber sigurnosti. Ovaj posjet će nam pružiti uvid u najnovije trendove, izazove i rješenja u oblasti, te će nam omogućiti interakciju s stručnjacima kako bismo proširili svoje znanje i razumijevanje ova važnog područja kompjuterske nauke.</font></p><p><font color=\"#9c9c94\"><br></font></p>', 1, '2024-05-10 18:29:56', '2024-05-20 20:07:34'),
(9, 'Studio Modra rijeka', 'Grbavička bb', 'Sarajevo', 21, 'https://maps.app.goo.gl/SJrBeQUZ7pU4tGA4A', 'f20bcad9ca35687f230b61b8d3f2fb25.png', '6ce0b4b736627700568c4c1059e4e773.jpg', 'c463f43eb4c68b4fd6067759d01dfc60.jpg', '<div style=\"text-align: justify;\"><span style=\"text-align: var(--bs-body-text-align);\"><font color=\"#f7f7f7\">Polaznici odsjeka za primijenjenu muzičku produkciju će imati priliku posjetiti i raditi u studiju Modra rijeka s gitaristom i kompozitorom Dinom Šukalom. Šukalo je radio kao kompozitor na filmovima Pjera Žalice, novoj seriji Srđana Vuletića, te će svoja iskustva u sound designu i kompoziciji za film i televiziju podijeliti s mladim profesionalcima koji se tek okušavaju u ovoj oblasti.</font></span></div>', 1, '2024-04-28 10:55:18', '2024-05-10 13:32:45'),
(10, 'Studio Chelia', 'Radićeva 2', 'Sarajevo', 21, 'https://maps.app.goo.gl/8SNLvU6DBkaccqAS7', 'f81c4cb7bb0b808bd96af472efbd4c53.png', 'c6447bdc1eacf1f7c4baa74ab6cfca41.jpg', 'a2f54d4efe876f6f8f08080030c05f4e.jpg', '<p class=\"MsoNormal\" style=\"text-align:justify;text-justify:inter-ideograph;\nline-height:150%\"><span style=\"white-space: pre; white-space: normal;\">	</span></p><p class=\"MsoNormal\" style=\"text-align:justify;text-justify:inter-ideograph;\nline-height:150%\"><span style=\"font-size: var(--bs-body-font-size); font-weight: var(--bs-body-font-weight); letter-spacing: 0px;\"><font color=\"#ffffff\">Studio Chelia, posvećen primarno audio post-produkciji, sadrži najsavremeniju audio opremu i najprofesionalnije uslove za rad. </font></span></p><p class=\"MsoNormal\" style=\"text-align:justify;text-justify:inter-ideograph;\nline-height:150%\"><span style=\"font-size: var(--bs-body-font-size); letter-spacing: 0px;\"><font color=\"#ffffff\"><span style=\"font-weight: var(--bs-body-font-weight);\">Polaznici odsjeka za primijenjenu muzičku produkciju će ovdje, pod mentorstvom </span><b style=\"\">Mirze Tahirovića</b><span style=\"font-weight: var(--bs-body-font-weight);\">, steći teoretska i praktična znanja o snimanju <i style=\"\">foley zvukova i sound designa za filmsko platno</i>.</span></font></span></p>', 1, '2024-05-09 14:33:45', '2024-05-16 10:30:06');
